import React, { useState } from "react";
import axios from "axios";

const ImageSelector = ({ images, downloadSelectedImages }) => {
  const [selectedIndexes, setSelectedIndexes] = useState([]);

  const handleCheckboxChange = (index) => {
    const selectedIndex = selectedIndexes.indexOf(index);
    let newSelectedIndexes = [...selectedIndexes];

    if (selectedIndex === -1) {
      newSelectedIndexes.push(index);
    } else {
      newSelectedIndexes.splice(selectedIndex, 1);
    }

    setSelectedIndexes(newSelectedIndexes);
  };

  const handleDownloadClick = async () => {
    const selectedImages = selectedIndexes.map((index) => ({
      url: images[index],
      filename: `${index + 1}.jpg`,
    }));

    try {
      await downloadSelectedImages(selectedImages);
    } catch (error) {
      console.error("Kesalahan saat mengunduh gambar:", error.message);
    }
  };

  return (
    <div>
      <h2>Pilih Gambar yang Ingin Diunduh</h2>
      {images.map((image, index) => (
        <div key={index}>
          <label>
            <input
              type="checkbox"
              checked={selectedIndexes.includes(index)}
              onChange={() => handleCheckboxChange(index)}
            />{" "}
            {index + 1}
          </label>
        </div>
      ))}
      <button onClick={handleDownloadClick}>Unduh Gambar Terpilih</button>
    </div>
  );
};

export default ImageSelector;
