import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function ImageViewDownload() {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the list of images from the backend
    axios
      .get("http://localhost:3001/downloads")
      .then((response) => {
        setImages(response.data.files);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching images:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="ImageViewDownload">
      <h1>Downloaded Images</h1>
      <div className="image-grid">
        {images.map((image) => (
          <div key={image} className="image-item">
            <img
              src={`http://localhost:3001/downloads/${image}`}
              alt={image}
              className="image"
            />
            <a href={`http://localhost:3001/downloads/${image}`} download>
              Download
            </a>
          </div>
        ))}
      </div>
      <div className="download-all">
        <a href="http://localhost:3001/download-all" download>
          Download All
        </a>
      </div>
    </div>
  );
}

export default ImageViewDownload;
