import React from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import imageData from "./excel-to-json.json"; // Pastikan file JSON ini ditempatkan di direktori src

const App = () => {
  const downloadImages = async () => {
    const images = imageData.Products.map((product) => product.item_image);
    const maxRetries = 7;
    const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

    for (let i = 0; i < images.length; i++) {
      let attempts = 0;
      let success = false;

      while (attempts < maxRetries && !success) {
        try {
          attempts++;
          if (!images[i] || !isValidUrl(images[i])) {
            throw new Error(`Invalid URL: ${images[i]}`);
          }
          const response = await axios.get(images[i], { responseType: "blob" });
          const fileName = `${i + 1}.jpg`; // Mengganti nama file mulai dari 1
          saveAs(response.data, fileName);
          success = true;
          console.log(`Berhasil mengunduh: ${fileName}`);
        } catch (error) {
          console.error(
            `Kesalahan mengunduh gambar ${
              i + 1
            }: ${error}. Percobaan ${attempts} dari ${maxRetries}`
          );
          if (attempts < maxRetries) {
            await delay(2000); // Menunggu 2 detik sebelum mencoba lagi
          }
        }
      }

      if (!success) {
        console.error(
          `Gagal mengunduh gambar ${i + 1} setelah ${maxRetries} percobaan.`
        );
      }
    }
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (e) {
      return false;
    }
  };

  return (
    <div>
      <h1>Pengunduh Gambar</h1>
      <button onClick={downloadImages}>Unduh Gambar</button>
    </div>
  );
};

export default App;
