import React, { useState } from "react";
import axios from "axios";

function ImageViewSelectedDownload() {
  const [zipDownloadLink, setZipDownloadLink] = useState("");
  const [message, setMessage] = useState("");

  const handleDownloadAll = async () => {
    try {
      const response = await axios.get("/downloadSelectedImage", {
        responseType: "blob", // Mengatur respons sebagai blob
      });

      // Membuat URL objek Blob untuk file zip
      const blob = new Blob([response.data], { type: "application/zip" });
      const url = window.URL.createObjectURL(blob);

      // Set link untuk download zip file
      setZipDownloadLink(url);
      setMessage("All images downloaded and zipped successfully!");
    } catch (error) {
      console.error("Error downloading zip file:", error);
      setMessage("Error downloading zip file.");
    }
  };

  return (
    <div className="ImageViewSelectedDownload">
      <h1>Download All Images as Zip</h1>
      <button onClick={handleDownloadAll}>Download All Images</button>
      {message && <p>{message}</p>}
      {zipDownloadLink && (
        <div>
          <p>
            <a href={zipDownloadLink} download="images.zip">
              Download Zip File
            </a>
          </p>
        </div>
      )}
    </div>
  );
}

export default ImageViewSelectedDownload;
