// src/App.js

import React from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import imageData from "./excel-to-json.json"; // Ensure this JSON file is placed in the src directory

const App = () => {
  const downloadImages = async () => {
    const images = imageData.Products.map((product) => product.item_image);

    for (let i = 0; i < images.length; i++) {
      try {
        const response = await axios.get(images[i], { responseType: "blob" });
        const fileName = `${i + 1}.jpg`; // Renaming files starting from 1
        saveAs(response.data, fileName);
      } catch (error) {
        console.error("Error downloading image:", error);
      }
    }
  };

  return (
    <div>
      <h1>Image Downloader</h1>
      <button onClick={downloadImages}>Download Images</button>
    </div>
  );
};

export default App;
