import React, { useState } from "react";
import axios from "axios";

function ImageViewSelectedDownload() {
  const [imageUrl, setImageUrl] = useState("");
  const [filename, setFilename] = useState("");
  const [message, setMessage] = useState("");
  const [zipDownloadLink, setZipDownloadLink] = useState("");

  const handleDownload = async () => {
    try {
      const response = await axios.post("/downloadSelectedImage", {
        url: imageUrl,
        filename: filename,
      });

      const { filePath } = response.data;
      setMessage("File downloaded successfully!");

      // Set download link for zip file
      setZipDownloadLink(filePath);
    } catch (error) {
      console.error("Error downloading file:", error);
      setMessage("Error downloading file.");
    }
  };

  const handleReset = () => {
    setImageUrl("");
    setFilename("");
    setMessage("");
    setZipDownloadLink("");
  };

  return (
    <div className="ImageViewSelectedDownload">
      <h1>Download Selected Images</h1>
      <div>
        <label>Image URL:</label>
        <input
          type="text"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)}
        />
      </div>
      <div>
        <label>Filename:</label>
        <input
          type="text"
          value={filename}
          onChange={(e) => setFilename(e.target.value)}
        />
      </div>
      <button onClick={handleDownload}>Download Image</button>
      <button onClick={handleReset}>Reset</button>
      {message && <p>{message}</p>}
      {zipDownloadLink && (
        <div>
          <p>
            <a href={zipDownloadLink} download>
              Download Zip File
            </a>
          </p>
        </div>
      )}
    </div>
  );
}

export default ImageViewSelectedDownload;
